import { useState } from "react";
import { useRouter } from "next/router";
import toast from "react-hot-toast";
import * as Yup from "yup";
import { useFormik } from "formik";
import {
  Box,
  Button,
  Card,
  CardContent,
  FormControlLabel,
  FormHelperText,
  Grid,
  MenuItem,
  Switch,
  TextField,
  Typography,
} from "@mui/material";
import { VehicleLogoDropzone } from "./vehicle-logo-dropzone";
import { QuillEditor } from "../../quill-editor";
import { useAuth } from "../../../hooks/use-auth";
import { getInitials } from "../../../utils/get-initials";

import { API, Storage } from "aws-amplify";
import { createVehicle } from "../../../graphql/mutations";
import { DataStore, Predicates } from "@aws-amplify/datastore";
import { Vehicle } from "../../../models";
import { v4 as uuid } from "uuid";
import { vehicleApi } from "../../../api/vehicle-api";
import { useDispatch } from "../../../store";
import { vehicleNumberFormatter } from "../../../utils/customFormatters";

export const VehicleCreateForm = (props) => {
  const router = useRouter();
  const dispatch = useDispatch();
  const [croppedImage, setCroppedImage] = useState();
  const [file, setFile] = useState();
  const { user } = useAuth();
  const formik = useFormik({
    initialValues: {
      vehicleNumber: "",
      make: "",
      model: "",
    },
    // validationSchema: Yup.object({
    //   name: Yup.string().max(255).required("Name is required"),
    // }),
    onSubmit: async (values, helpers) => {
      try {
        // NOTE: Make API request
        const id = uuid();
        values.id = id;
        values.user = user.id;
        console.log(values);
        await vehicleApi.createVehicle(values, dispatch);

        toast.success("Vehicle created!");
        // router.push("/dashboard/vehicles");
      } catch (err) {
        console.error(err);
        toast.error("Something went wrong!");
        helpers.setStatus({ success: false });
        helpers.setErrors({ submit: err.message });
        helpers.setSubmitting(false);
      }
    },
  });

  return (
    <form onSubmit={formik.handleSubmit} {...props}>
      <Card>
        <CardContent>
          <Grid container spacing={3}>
            <Grid item md={4} xs={12}>
              <Typography variant="h6">Truck details</Typography>
            </Grid>
            <Grid item md={8} xs={12}>
              <Grid container spacing={3}>
                <Grid item xs={12}>
                  <TextField
                    error={Boolean(
                      formik.touched.vehicleNumber &&
                        formik.errors.vehicleNumber
                    )}
                    fullWidth
                    helperText={
                      formik.touched.vehicleNumber &&
                      formik.errors.vehicleNumber
                    }
                    label="Vehicle Number"
                    name="vehicleNumber"
                    onBlur={formik.handleBlur}
                    onChange={formik.handleChange}
                    value={formik.values.vehicleNumber}
                    variant="outlined"
                    inputProps={{ style: { textTransform: "uppercase" } }}
                    InputProps={{
                      inputComponent: vehicleNumberFormatter,
                    }}
                  />

                  {/* 
                    onChange={event => {
                      handleChange(event);
                    }} */}
                </Grid>
                <Grid item md={6} xs={12}>
                  <TextField
                    error={Boolean(formik.touched.make && formik.errors.make)}
                    fullWidth
                    helperText={formik.touched.make && formik.errors.make}
                    label="Make"
                    name="make"
                    onBlur={formik.handleBlur}
                    onChange={formik.handleChange}
                    value={formik.values.make}
                    variant="outlined"
                  />
                </Grid>
                <Grid item md={6} xs={12}>
                  <TextField
                    error={Boolean(formik.touched.model && formik.errors.model)}
                    fullWidth
                    helperText={formik.touched.model && formik.errors.model}
                    label="Model"
                    name="model"
                    onBlur={formik.handleBlur}
                    onChange={formik.handleChange}
                    value={formik.values.model}
                    variant="outlined"
                  />
                </Grid>
              </Grid>
            </Grid>
          </Grid>
        </CardContent>
      </Card>

      <Box
        sx={{
          display: "flex",
          flexWrap: "wrap",
          justifyContent: "space-between",
          mx: -1,
          mb: -1,
          mt: 3,
        }}
      >
        <Button
          color="error"
          sx={{
            m: 1,
            mr: "auto",
          }}
        >
          Delete
        </Button>
        <Button sx={{ m: 1 }} variant="outlined">
          Cancel
        </Button>
        <Button sx={{ m: 1 }} type="submit" variant="contained">
          Create
        </Button>
      </Box>
    </form>
  );
};
