/* eslint-disable no-use-before-define */
import React, { useState, useEffect, useCallback } from "react";
import * as Yup from "yup";
import { useFormik } from "formik";

import {
  Box,
  Button,
  Card,
  CardContent,
  Dialog,
  DialogTitle,
  Grid,
  Switch,
  TextField,
  Typography,
} from "@mui/material";
import { useDispatch } from "../../../store";
import GoogleMaps from "../welcome/google-places-autocomplete";
import { partyApi } from "../../../api/party-api";
import { FormatLineSpacing } from "@mui/icons-material";

const AddNewPartyFromAutocomplete = ({
  open,
  dialogValue,
  setDialogValue,
  toggleOpen,
  type,
  user,
  ...rest
}) => {
  const handleClose = () => {
    setDialogValue({
      name: "",
      transporter: type !== "customer" ? true : false,
    });

    toggleOpen(false);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    setValue({
      name: dialogValue.name,
      transporter: dialogValue.transporter,
    });

    try {
      await partyApi.createParty(
        {
          name: dialogValue.name.replace(/\w\S*/g, function (txt) {
            return txt.charAt(0).toUpperCase() + txt.substr(1).toLowerCase();
          }),
          transporter: dialogValue.transporter,
        },
        dispatch
      );
      setFieldValue(type, response.data);
    } catch (err) {
      console.error(err);
    }

    handleClose();
  };

  let formik = useFormik({
    initialValues: {
      name: dialogValue.name,
      mobile: dialogValue.mobile || "",
      location: dialogValue.location || "",
      transporter: dialogValue.transporter || "",
      submit: null,
    },
    validationSchema: Yup.object().shape({
      name: Yup.string().max(255).required("Name is required"),
      mobile: Yup.string()
        .matches(/^[6-9]\d{9}$/, "Phone number is not valid")
        .required("Mobile is required"),
    }),
    onSubmit: async (
      values,
      { resetForm, setErrors, setStatus, setSubmitting }
    ) => {
      try {
        // NOTE: Make API request
        console.log(values);
        const response = await axios.post(`/api/parties/`, {
          name: values.name.replace(/\w\S*/g, function (txt) {
            return txt.charAt(0).toUpperCase() + txt.substr(1).toLowerCase();
          }),
          ...values,
        });
        setFieldValue(type, response.data);
        // dispatch(updateParty(party, values));
        resetForm();
        setStatus({ success: true });
        setSubmitting(false);
        handleClose();
      } catch (err) {
        console.log(err);
        setStatus({ success: false });
        setErrors({ submit: err.message });
        setSubmitting(false);
      }
    },
  });
  console.log(dialogValue.name);
  useEffect(async () => {
    try {
      formik.setFieldValue("name", dialogValue.name);
    } catch (error) {
      console.log(error);
    }
  }, []);
  console.log(formik.values);

  return (
    <React.Fragment>
      <Dialog
        open={open}
        onClose={handleClose}
        aria-labelledby="form-dialog-name"
      >
        <DialogTitle id="form-dialog-name">Add a new party</DialogTitle>
        <form onSubmit={handleSubmit} {...rest}>
          <Card>
            <CardContent>
              <Grid container spacing={3}>
                <Grid item xs={12}>
                  <TextField
                    error={Boolean(formik.touched.name && formik.errors.name)}
                    fullWidth
                    helperText={formik.touched.name && formik.errors.name}
                    label="Main Contact Name"
                    name="name"
                    onBlur={formik.handleBlur}
                    onChange={(event) => {
                      formik.setFieldValue(
                        "name",
                        event.target.value.replace(/\w\S*/g, function (txt) {
                          return (
                            txt.charAt(0).toUpperCase() +
                            txt.substr(1).toLowerCase()
                          );
                        })
                      );
                    }}
                    required
                    value={formik.values.name}
                    variant="outlined"
                  />
                </Grid>
                <Grid item xs={12}>
                  <TextField
                    error={Boolean(
                      formik.touched.mobile && formik.errors.mobile
                    )}
                    fullWidth
                    helperText={formik.touched.mobile && formik.errors.mobile}
                    label="Mobile"
                    name="mobile"
                    onBlur={formik.handleBlur}
                    onChange={(event) => {
                      setFieldValue(
                        "mobile",
                        event.target.value.replace(/ /g, "")
                      );
                    }}
                    value={formik.values.mobile}
                    variant="outlined"
                  />
                </Grid>
                <Grid item xs={12}>
                  <GoogleMaps
                    name={"location"}
                    field={"location"}
                    formik={formik}
                    error={Boolean(formik.touched.city && formik.errors.city)}
                    label={"Your city in India"}
                    setFieldValue={formik.setFieldValue}
                    handleBlur={formik.handleBlur}
                    values={formik.values}
                  />
                </Grid>
                <Grid item xs={12}>
                  <Typography variant="h5" color="textPrimary">
                    Party Type
                  </Typography>
                  <Typography variant="body2" color="textSecondary">
                    Is this party a transporter?
                  </Typography>
                  <Switch
                    checked={formik.values.transporter}
                    color="secondary"
                    edge="start"
                    name="transporter"
                    onChange={formik.handleChange}
                    value={formik.values.transporter}
                  />
                </Grid>
              </Grid>
              <Box mt={2}>
                <Grid container spacing={3}>
                  <Grid item>
                    <Button
                      variant="contained"
                      color="secondary"
                      type="submit"
                      disabled={formik.isSubmitting}
                    >
                      Create Party
                    </Button>
                  </Grid>
                  <Grid item>
                    <Button
                      variant="contained"
                      color="secondary"
                      onClick={handleClose}
                    >
                      Cancel
                    </Button>
                  </Grid>
                </Grid>
              </Box>
            </CardContent>
          </Card>
        </form>
      </Dialog>
    </React.Fragment>
  );
};
export default AddNewPartyFromAutocomplete;
