import PropTypes from "prop-types";
import {
  Button,
  Card,
  CardActions,
  CardHeader,
  Divider,
  useMediaQuery,
} from "@mui/material";
import { PropertyList } from "../../property-list";
import { PropertyListItem } from "../../property-list-item";
import { useDispatch } from "../../../store";
import GoogleMaps from "./google-places-autocomplete";

import React from "react";

export const AddressCard = (props) => {
  const { address } = props;
  const mdUp = useMediaQuery((theme) => theme.breakpoints.up("md"));

  const align = mdUp ? "horizontal" : "vertical";

  const [status, setStatus] = useState(true);
  const toggleStatus = () => {
    setStatus(!status);
  };

  let addressCard = (
    <React.Fragment>
      <Card {...other}>
        <CardHeader
          title={address.name}
          action={
            <Button variant="outlined" color="primary" onClick={toggleStatus}>
              Edit
            </Button>
          }
        />
        <Divider />
        <PropertyList>
          <PropertyListItem
            align={align}
            divider
            label="GSTIN"
            value={address.gstin}
          />
          <PropertyListItem
            align={align}
            divider
            label="PAN"
            value={address.pan}
          />
          <PropertyListItem
            align={align}
            divider
            label="Address Line 1"
            value={address.billingAddressLine1}
          />
          <PropertyListItem
            align={align}
            divider
            label="Address Line 2"
            value={address.billingAddressLine2}
          />
          <PropertyListItem
            align={align}
            disableGutters
            label="City"
            value={JSON.parse(address.city).description}
          />
        </PropertyList>
      </Card>
    </React.Fragment>
  );

  const PartyAddressForm = (props) => {
    const { onCancel, address } = props;
    const dispatch = useDispatch();

    const formik = useFormik({
      initialValues: {
        name: address.name || "",
        gstin: address.gstin || "",
        pan: address.pan || "",
        billingAddressLine1: address.billingAddressLine1 || "",
        billingAddressLine2: address.billingAddressLine2 || "",
        city: address.city || "",
        submit: null,
      },
      onSubmit: async (values, helpers) => {
        try {
          // NOTE: Make API request
          type === "new"
            ? await partyApi.createPartyAddress(values, dispatch)
            : await partyApi.updatePartyAddress(values, dispatch);
          toggleStatus();

          toast.success("Party updated!");
          onCancel();
        } catch (err) {
          console.error(err);
          toast.error("Something went wrong!");
          helpers.setStatus({ success: false });
          helpers.setErrors({ submit: err.message });
          helpers.setSubmitting(false);
        }
      },
    });

    return (
      <>
        <form onSubmit={formik.handleSubmit} {...props}>
          <Box
            sx={{
              alignItems: "center",
              backgroundColor: (theme) =>
                theme.palette.mode === "dark" ? "neutral.800" : "neutral.100",
              bpartyRadius: 1,
              display: "flex",
              flexWrap: "wrap",
              justifyContent: "space-between",
              px: 3,
              py: 2.5,
            }}
          >
            <Typography variant="overline" sx={{ mr: 2 }} color="textSecondary">
              Party
            </Typography>
            <Box
              sx={{
                alignItems: "center",
                display: "flex",
                m: -1,
                "& > button": {
                  m: 1,
                },
              }}
            >
              <Button
                color="primary"
                type="submit"
                size="small"
                variant="contained"
              >
                Save changes
              </Button>
              <Button onClick={onCancel} size="small" variant="outlined">
                Cancel
              </Button>
            </Box>
          </Box>

          <Typography sx={{ my: 3 }} variant="h6">
            Details
          </Typography>

          <TextField
            margin="normal"
            error={Boolean(formik.touched.name && formik.errors.name)}
            fullWidth
            helperText={formik.touched.name && formik.errors.name}
            label="Name"
            name="name"
            onBlur={formik.handleBlur}
            onChange={(event) => {
              formik.setFieldValue(
                "name",
                event.target.value.replace(/\w\S*/g, function (txt) {
                  return (
                    txt.charAt(0).toUpperCase() + txt.substr(1).toLowerCase()
                  );
                })
              );
            }}
            value={formik.values.name}
            variant="outlined"
          />
          <TextField
            error={Boolean(formik.touched.gstin && formik.errors.gstin)}
            fullWidth
            helperText={formik.touched.gstin && eformik.rrors.gstin}
            label="GSTIN"
            name="gstin"
            onBlur={formik.handleBlur}
            onChange={formik.handleChange}
            value={formik.values.gstin}
            variant="outlined"
          />
          <TextField
            error={Boolean(formik.touched.pan && formik.errors.pan)}
            fullWidth
            helperText={formik.touched.pan && eformik.rrors.pan}
            label="PAN"
            name="pan"
            onBlur={formik.handleBlur}
            onChange={formik.handleChange}
            value={formik.values.pan}
            variant="outlined"
          />
          <TextField
            margin="normal"
            error={Boolean(
              formik.touched.billingaddressline1 &&
                formik.errors.billingaddressline1
            )}
            fullWidth
            helperText={
              formik.touched.billingaddressline1 &&
              formik.errors.billingaddressline1
            }
            label="Billing Address Line 1"
            name="billingaddressline1"
            onBlur={formik.handleBlur}
            onChange={formik.handleChange}
            value={formik.values.billingAddressLine1}
            variant="outlined"
          />
          <TextField
            margin="normal"
            error={Boolean(
              formik.touched.billingaddressline2 &&
                formik.errors.billingaddressline2
            )}
            fullWidth
            helperText={
              formik.touched.billingaddressline2 &&
              formik.errors.billingaddressline2
            }
            label="Billing Address Line 2"
            name="billingaddressline2"
            onBlur={formik.handleBlur}
            onChange={formik.handleChange}
            value={formik.values.billingAddressLine2}
            variant="outlined"
          />
          <GoogleMaps
            formik={formik}
            error={Boolean(formik.touched.city && formik.errors.city)}
            label={"City"}
            field={"city"}
            setFieldValue={formik.setFieldValue}
            handleBlur={formik.handleBlur}
            values={formik.values}
          />
          <Box mt={2}>
            <Button
              style={{ marginRight: 10 }}
              variant="contained"
              color="secondary"
              type="submit"
              disabled={isSubmitting}
            >
              {type === "new" ? "Create Party" : "Update Party"}
            </Button>

            <Button variant="contained" color="primary" onClick={toggleStatus}>
              Cancel
            </Button>
          </Box>
        </form>
      </>
    );
  };

  return status ? { addressCard } : { PartyAddressForm };
};
