import { useState, useEffect } from "react";
import PropTypes from "prop-types";
import { format } from "date-fns";
import { Storage } from "aws-amplify";
import {
  Box,
  Button,
  Divider,
  Drawer,
  IconButton,
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableRow,
  TextField,
  Typography,
  useMediaQuery,
} from "@mui/material";
import { styled } from "@mui/material/styles";
import EditIcon from "@mui/icons-material/Edit";
import { X as XIcon } from "../../../icons/x";
import { PropertyList } from "../../property-list";
import { PropertyListItem } from "../../property-list-item";
import { Scrollbar } from "../../scrollbar";

const OrganisationPreview = (props) => {
  const { lgUp, onApprove, onEdit, onReject, organisation } = props;
  const align = lgUp ? "horizontal" : "vertical";
  const [organisationLogo, setOrganisationLogo] = useState();

  console.log(organisation);

  useEffect(async () => {
    try {
      const logo = await Storage.get(organisation.logo);
      console.log(logo);
      setOrganisationLogo(logo);
    } catch (error) {
      console.log(error);
    }
  }, []);

  return (
    <>
      {organisationLogo && (
        <img
          style={{ width: "675px", height: "150px" }}
          src={organisationLogo}
          alt="Business Logo"
        />
      )}
      <Box
        sx={{
          alignItems: "center",
          borderRadius: 1,
          display: "flex",
          flexWrap: "wrap",
          justifyContent: "space-between",
        }}
      >
        <Typography sx={{ mt: 3 }} variant="h6">
          Details
        </Typography>
        <Box
          sx={{
            alignItems: "center",
            display: "flex",
            flexWrap: "wrap",
            mt: 3,
            m: -1,
            "& > button": {
              m: 1,
            },
          }}
        >
          <Button
            onClick={onEdit}
            size="small"
            startIcon={<EditIcon fontSize="small" />}
            sx={{ pt: 3 }}
          >
            Edit
          </Button>
        </Box>
      </Box>
      <Divider sx={{ my: 3 }} />

      <PropertyList>
        <PropertyListItem align={align} disableGutters label="Business Name">
          <Typography color="primary" variant="body2">
            {`${organisation.name} (${organisation.initials})`}
          </Typography>
          <Typography color="textSecondary" variant="body2">
            {organisation.addressLine1}
          </Typography>
          <Typography color="textSecondary" variant="body2">
            {organisation.addressLine2}
          </Typography>
          <Typography color="textSecondary" variant="body2">
            {organisation.city}
