import * as React from "react";
import { useRef } from "react";
import {
  Box,
  Button,
  Card,
  CardContent,
  Divider,
  Grid,
  Typography,
} from "@mui/material";
import { Briefcase as BriefcaseIcon } from "../../../icons/briefcase";
import { useTranslation } from "react-i18next";
import { BranchCreateForm } from "../../../components/dashboard/welcome/branch-create-form";

export default function HorizontalNonLinearStepper() {
  const branchCreateFormRef = useRef(null);
  const { t } = useTranslation();

  const handleNext = () => {
    branchCreateFormRef.current.handleFormSubmit();
  };

  const createAMainBranch = (
    <React.Fragment>
      <Grid item xs={12} sx={{ py: 2 }}>
        <Card>
          <CardContent>
            <Box
              sx={{
                alignItems: "center",
                display: "flex",
              }}
            >
              <BriefcaseIcon color="primary" fontSize="small" />
              <Typography
                color="primary.main"
                sx={{ pl: 1 }}
                variant="subtitle2"
              >
                {t("Add your main branch details")}
              </Typography>
            </Box>
            <Typography variant="h5" sx={{ mt: 2 }}>
              {t("Let us open your first branch.")}
            </Typography>
            <Box
              sx={{
                alignItems: "center",
                display: "flex",
              }}
            >
              <BranchCreateForm
                handleNext={handleNext}
                ref={branchCreateFormRef}
              />
            </Box>
          </CardContent>
          <Divider />
        </Card>
      </Grid>
    </React.Fragment>
  );

  return (
    <Box sx={{ minWidth: "100%" }}>
      {createAMainBranch}
      <Box sx={{ display: "flex", flexDirection: "row", pt: 2 }}>
        <Box sx={{ flex: "1 1 auto" }} />
        <Button
          component="a"
          variant="contained"
          onClick={handleNext}
          sx={{ ml: 1 }}
        >
          Create Branch
        </Button>
      </Box>
    </Box>
  );
}
