import { useEffect, useState } from "react";
import Head from "next/head";
import NextLink from "next/link";
import { useRouter } from "next/router";
import {
  Box,
  Button,
  Card,
  CardActions,
  CardContent,
  Container,
  Divider,
  Grid,
  MenuItem,
  TextField,
  Typography,
} from "@mui/material";
import { useTranslation } from "react-i18next";
import { AuthGuard } from "../../../components/authentication/auth-guard";
import { DashboardLayout } from "../../../components/dashboard/dashboard-layout";
import Stepper from "../../../components/dashboard/welcome/stepper";
import { ArrowRight as ArrowRightIcon } from "../../../icons/arrow-right";
import { Briefcase as BriefcaseIcon } from "../../../icons/briefcase";
import { Download as DownloadIcon } from "../../../icons/download";
import { ExternalLink as ExternalLinkIcon } from "../../../icons/external-link";
import { InformationCircleOutlined as InformationCircleOutlinedIcon } from "../../../icons/information-circle-outlined";
import { Users as UsersIcon } from "../../../icons/users";
import { gtm } from "../../../lib/gtm";
import { useAuth } from "../../../hooks/use-auth";

const Overview = () => {
  const [displayBanner, setDisplayBanner] = useState(true);
  const { user } = useAuth();
  const { t } = useTranslation();
  //   const router = useRouter();

  useEffect(() => {
    gtm.push({ event: "page_view" });
  }, []);

  useEffect(() => {
    // Restore the persistent state from local/session storage
    if (user.onBoardingRequired === true) {
      //   console.log(user.onBoardingRequired);
      // router.push({
      //   pathname: "/dashboard/welcome",
      //   query: { returnUrl: router.asPath },
      // });
      // setDisplayBanner(false);
    }
  }, []);

  const handleDismissBanner = () => {
    // Update the persistent state
    // globalThis.sessionStorage.setItem('dismiss-banner', 'true');
    setDisplayBanner(false);
  };

  return (
    <>
      <Head>
        <title>Dashboard: Overview | Material Kit Pro</title>
      </Head>
      <Box
        component="main"
        sx={{
          flexGrow: 1,
          py: 8,
        }}
      >
        <Container maxWidth="xl">
          <Box sx={{ mb: 4 }}>
            <Grid container justifyContent="space-between" spacing={3}>
              <Grid item>
                <Typography variant="h4">{`${t("Welcome")} ${
                  user.name
                },`}</Typography>
                <Typography sx={{ mt: 2 }} variant="h6">
                  {t(
                    "We are so happy to have you with us. Let us help you figure things out."
                  )}
                </Typography>
              </Grid>
            </Grid>
          </Box>
          <Box sx={{ mb: 4 }}>
            <Grid container justifyContent="space-between" spacing={3}>
              <Grid item>
                <Stepper />
              </Grid>
            </Grid>
          </Box>
        </Container>
      </Box>
    </>
  );
};

Overview.getLayout = (page) => (
  <AuthGuard>
    <DashboardLayout>{page}</DashboardLayout>
  </AuthGuard>
);

export default Overview;
