import React, { useState, useRef, useMemo, useCallback } from "react";
import {
  GoogleMap,
  Marker,
  DirectionsService,
  DirectionsRenderer,
} from "@react-google-maps/api";

const GoogleMaps = ({ addresses }) => {
  console.log("child component");

  const [googleResponse, setResponse] = useState(null);

  let directionsCallback = (response) => {
    if (response !== null) {
      if (response.status === "OK") {
        if (JSON.stringify(googleResponse) === JSON.stringify(response)) {
          return;
        } else {
          setResponse(response);
        }
      } else {
        console.log("response: ");
      }
    }
  };

  return (
    <GoogleMap
      options={{ mapTypeId: "hybrid", disableDefaultUI: true }}
      mapContainerStyle={{
        width: "90%",
        height: "100%",
        minHeight: "12rem",
      }}
      center={{
        lat: 22.309425,
        lng: 72.13623,
      }}
      zoom={5}
    >
      {addresses.origin && addresses.destination && (
        <DirectionsService
          // required
          options={{
            origin: addresses.origin,
            destination: addresses.destination,
            waypoints: addresses.waypoints,
            travelMode: "DRIVING",
            optimizeWaypoints: true,
          }}
          // required
          callback={directionsCallback}
        />
      )}

      {
        <DirectionsRenderer
          // required
          options={{
            // eslint-disable-line react-perf/jsx-no-new-object-as-prop
            directions: googleResponse,
          }}
        />
      }
      {/* {formik.values.deliveryDetails.map((deliveries) => {
        return (
          <React.Fragment>
            <Marker
              key={deliveries.loading.place_id}
              position={{
                lat: deliveries.loading.latitude,
                lng: deliveries.loading.longitude,
              }}
            />
            <Marker
              key={deliveries.unloading.place_id}
              position={{
                lat: deliveries.unloading.latitude,
                lng: deliveries.unloading.longitude,
              }}
            />
          </React.Fragment>
        );
      })} */}
    </GoogleMap>
  );
};

export default React.memo(GoogleMaps);
