import { useState } from "react";
import { useRouter } from "next/router";
import toast from "react-hot-toast";
import * as Yup from "yup";
import { useFormik } from "formik";
import {
  Box,
  Button,
  Card,
  CardContent,
  Divider,
  FormControlLabel,
  FormHelperText,
  Grid,
  MenuItem,
  Switch,
  TextField,
  Typography,
} from "@mui/material";
import { FileDropzone } from "../../file-dropzone";
import { QuillEditor } from "../../quill-editor";
import GoogleMaps from "./google-places-autocomplete";
const categoryOptions = [
  {
    label: "Healthcare",
    value: "healthcare",
  },
  {
    label: "Makeup",
    value: "makeup",
  },
  {
    label: "Dress",
    value: "dress",
  },
  {
    label: "Skincare",
    value: "skincare",
  },
  {
    label: "Jewelry",
    value: "jewelry",
  },
  {
    label: "Blouse",
    value: "blouse",
  },
];

export const BranchCreateForm = (props) => {
  const router = useRouter();
  const [files, setFiles] = useState([]);
  const formik = useFormik({
    initialValues: {
      name: "",
      addressLine1: "",
      addressLine2: "",
      city: "",
      submit: null,
    },
    validationSchema: Yup.object({
      name: Yup.string().max(255).required("Name is required"),
      addressLine1: Yup.string()
        .max(255)
        .required("Address Line 1 is required"),
      city: Yup.object().required("City is required"),
    }),
    onSubmit: async (values, helpers) => {
      try {
        // NOTE: Make API request
        toast.success("Branch created!");
        // router.push("/dashboard/branches");
      } catch (err) {
        console.error(err);
        toast.error("Something went wrong!");
        helpers.setStatus({ success: false });
        helpers.setErrors({ submit: err.message });
        helpers.setSubmitting(false);
      }
    },
  });

  const handleDrop = (newFiles) => {
    setFiles((prevFiles) => [...prevFiles, ...newFiles]);
  };

  const handleRemove = (file) => {
    setFiles((prevFiles) =>
      prevFiles.filter((_file) => _file.path !== file.path)
    );
  };

  const handleRemoveAll = () => {
    setFiles([]);
  };

  return (
    <form onSubmit={formik.handleSubmit} {...props}>
      <Grid container spacing={3} alignItems="center" sx={{ mb: 4 }}>
        <Grid item md={4} xs={12}>
          <Typography variant="h6">Tell us your city</Typography>
        </Grid>
        <Grid item md={8} xs={12}>
          {/* <TextField
                error={Boolean(formik.touched.name && formik.errors.name)}
                fullWidth
                helperText={formik.touched.name && formik.errors.name}
                label="Branch Name"
                name="name"
                onBlur={formik.handleBlur}
                onChange={formik.handleChange}
                value={formik.values.name}
              /> */}
          <GoogleMaps
            formik={formik}
            error={Boolean(formik.touched.city && formik.errors.city)}
            label={"Your city in India"}
            field={"city"}
            setFieldValue={formik.setFieldValue}
            handleBlur={formik.handleBlur}
            values={formik.values}
          />
        </Grid>

        <Grid item md={4} xs={12}>
          <Typography variant="h6">Please pick a name</Typography>
        </Grid>
        <Grid item md={8} xs={12}>
          <TextField
            error={Boolean(
              formik.touched.branchname && formik.errors.branchname
            )}
            fullWidth
            helperText={formik.touched.branchname && formik.errors.branchname}
            label="Your branch name"
            name="branchname"
            onBlur={formik.handleBlur}
            onChange={formik.handleChange}
            value={formik.values.branchname}
          />
        </Grid>
      </Grid>
    </form>
  );
};
